<script>
import CountryInfo from "../components/CountryInfo"

export default {
  name: "Europe",
  components: {
    CountryInfo
  },
  data() {
    return {
      name: 'Australie',
      nativeName: 'Australia',
      capital: 'Canberra',
      flag: 'https://restcountries.eu/data/aus.svg',
      population: 24117360,
      area: 7692024,
      currenciesName: 'Australian dollar',
      currenciesSymbol: '$',
    }
  },
  beforeMount(){
    this.getCountry();
  },
  methods: {
    async getCountry(event){
      const res = await fetch(`https://restcountries.eu/rest/v2/alpha/${event.currentTarget.id}`);
      const data = await res.json();
      this.data = data;
      console.log(this.data);
      this.name = data.translations.fr;
      this.nativeName = data.nativeName;
      this.capital = data.capital;
      this.flag = data.flag;
      this.population = data.population;
      this.area = data.area;
      this.currenciesName = data.currencies[0].name;
      this.currenciesSymbol = data.currencies[0].symbol;
    },
  }
};
</script>
